              msg.put("proxie_security", JsonOutput.toJson(proxie_security));
              msg.put("proxie_authentication", JsonOutput.toJson(proxie_authentication));
              msg.put("proxie_inbound", proxie_inbound);
              msg.put("proxie_outbound", proxie_outbound);
              msg.put("proxie_serviceProfiles", proxie_serviceProfiles);
              msg.put("proxie_caCerts", JsonOutput.toJson(proxie_caCerts));

