def invoke(msg) {

       def slurper = new JsonSlurper();

       String operation_id_old = new String("");
       String operation_id_new = new String("");

       operation_id_old = msg.get("operation_id_old");

       String api_operation_new = new String(msg.get("api_operation_new"));
       String api_operation_name = new String(msg.get("api_operation_name"));

       ArrayList<String> operationsArrayList = slurper.parseText(api_operation_new);

       for (int i = 0; i < operationsArrayList.size(); ++i)
       {
          if (api_operation_name == operationsArrayList[i].name)
          {
              operation_id_new = operationsArrayList[i].id;
              break;
          }
       }

        msg.put("proxie_inbound", msg.get("proxie_inbound").replace(operation_id_old, operation_id_new));

        return true;
}

