    def data = msg.get("data");
    def base64EncodedData = Encoder.encode(data);
    msg.put("data.base64", base64EncodedData);
    return true;        
}

import com.vordel.mime.XMLBody;

def invoke(msg)        
{ 
    def doc = XMLBody.getDocument(msg);
    return true;         
}

var base64Import = new JavaImporter(com.vordel.common.base64);
with(base64Import) {
    function invoke(msg) {

        var base64EncodedData = msg.get("data.base64");
        var dataDecoded = Decoder.decodeToString(base64EncodedData);
        msg.put("data.decoded", dataDecoded);
        return true;
    }

};

def invoke(msg) 
{ 
    def userProperties = msg.get("attribute.lookup.list");

    if (userProperties == null)
        return true;
    userProperties.remove("role");       
    return true;         
}

from com.vordel.common.base64 import Encoder

def invoke(msg): 
    data = msg.get("data");
    if data != None:
        dataEncoded = Encoder.encode(data);
        msg.put("data.base64", dataEncoded );
    return True

function invoke(msg) {
    var userProperties = msg.get("attribute.lookup.list");

    if (userProperties == null) {
        userProperties = new java.util.HashMap();
        msg.put("attribute.lookup.list", userProperties);
    }
    var role = userProperties.get("role");
    return role == "Manager";
}

importPackage(Packages.com.vordel.common.base64);

function invoke(msg)         
{        
    var data = msg.get("data");
    var base64EncodedData = Encoder.encode(data);
    msg.put("data.base64", base64EncodedData);
    return true;        
}

def invoke(msg) 
{ 
    def httpHeaders = msg.get("http.headers");
    def action = httpHeaders.get("SOAPAction");
    return action == "test";
}

def invoke(msg):
    httpHeaders = msg.get("http.headers")
    action = httpHeaders.get("SOAPAction")
    if action != None:
        return action.equals("test")
    return False

